/**
 * @name nextTick
 * @description Defer the operation to the queue for evaluation on the next tick
 */
export function nextTick(onExec, onError) {
  Promise.resolve().then(() => {
    onExec();
  }).catch(error => {
    if (onError) {
      onError(error);
    } else {
      console.error(error);
    }
  });
}